import React, { useEffect, useState } from 'react';
import './create_event_template.css';
import { Col, Row, Form, Button, Dropdown } from 'react-bootstrap';
import swal from 'sweetalert';
import axios from 'axios';

const Create_event_template = () => {
  const [value1, setValue1] = useState('');
  const [value2, setValue2] = useState('');
  const [value3, setValue3] = useState();
  const [serviceNames, setServiceNames] = useState([]);
  const [searchValue, setSearchValue] = useState('');
  const [selectedServices, setSelectedServices] = useState([]);
  const [alreadySelected, setAlreadySelected] = useState([]);
  const [filteredServiceNames, setFilteredServiceNames] = useState([]);
 
  useEffect(() => {
    // Fetch service names from the backend API
    fetch('http://localhost:3001/gtservicenames')
      .then((response) => response.json())
      .then((data) => {
        setServiceNames(data);
        setFilteredServiceNames(data);
      });
  }, []);

  const handleSearchChange = (event) => {
    const searchQuery = event.target.value;
    setSearchValue(searchQuery);

    const filteredServices = serviceNames.filter((name) =>
      name.serviceName.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredServiceNames(filteredServices);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();

    // Check if the selected service is already in the selectedServices array
    if (selectedServices.includes(searchValue)) {
      swal({
        title: 'Service already selected!',
        icon: 'warning',
        buttons: 'OK',
        className: 'custom-alert',
      });
      return;
    }

    // Add the selected service to the selectedServices array
    setSelectedServices([...selectedServices, searchValue]);
    setSearchValue('');

    // Update the alreadySelected state
    setAlreadySelected([...alreadySelected, searchValue]);

    // Perform search based on the searchValue
    console.log('Perform search:', searchValue);
    // Add your search logic here
  };

  const handleDropService = (service) => {
    // Remove the service from selectedServices
    const updatedSelectedServices = selectedServices.filter(
      (selectedService) => selectedService !== service
    );
    setSelectedServices(updatedSelectedServices);

    // Remove the service from alreadySelected
    const updatedAlreadySelected = alreadySelected.filter(
      (selectedService) => selectedService !== service
    );
    setAlreadySelected(updatedAlreadySelected);
  };

  const onSubmit = (event) => {
    event.preventDefault();

    //  const from = event.currentTarget()
    // console.log(value1)
    //  values.preventDefault()
    const eData = {
          
      eventTemplateName: value1,////////////////////////////
      eventTemplateDescrpt:value2
    };

    axios
      .post("http://localhost:3001/evnttmplt/snd_data",eData )
      .then((response) => {
        return axios.get("http://localhost:3001/evnttmplt/lastId");
      })
      .then((response) => {
        const lastId = response.data.eventTemplateid;
        console.log(response.data.eventTemplateid);
        
        console.log(selectedServices)
        const templateData = {
          
          eventTemplateid: lastId,////////////////////////////
          serviceId:parseInt(value3) ,
        };
        
        axios.post("http://localhost:3001/pstevnttmpltservices", templateData);
      })
      .then(() => {
      
       // resetForm();
       
      })
      .catch((error) => {
        
        console.log(error);
        alert("Error : ");
      });
  };



  return (
    <div style={{ margin: '8% 8% 0% 3%', padding: '0% 8%' }}>
      <h1 className='create_event_template_heading'>Add Event Template</h1>

      <Form onSubmit={onSubmit} >
        <Row>
          <Col>
            <Form.Group controlId='EventTemplate'>
              <Form.Label>Event Template Name:</Form.Label>
              <Form.Control type='text' className='custom-input' value={value1} onChange={(e)=>setValue1(e.target.value)}/>
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col>
            <Form.Group controlId='EventDescription'>
              <Form.Label>Template Description:</Form.Label>
              <Form.Control as='textarea' className='custom-input' value={value2} onChange={(e)=>setValue2(e.target.value)}/>
            </Form.Group>
          </Col>
        </Row>

        <Row>
          <Col xs={12} lg={6}>
            <Form.Group controlId='EventTmpltService'>
              <Form.Label>Select Services:</Form.Label>
              <Dropdown>
                <Dropdown.Toggle variant='secondary' className='custom-dropdown-toggle'>
                  {searchValue || 'Select a service'}
                </Dropdown.Toggle>
                <Dropdown className='custom-dropdown-menu'>
                  <Form.Control
                    type='text'
                    placeholder='Search'
                    className='custom-input'
                    aria-label='Search'
                   // value={searchValue}
                   // onChange={handleSearchChange}
                    value={value3} onChange={(e)=>setValue3(e.target.value)}
                  />
                  {filteredServiceNames.map((name) => (
                    <Dropdown.Item
                    
                      key={name.serviceName}
                      
                     // onClick={() => setSearchValue(name.serviceName)}
                    >
                      {name.serviceName}
                    </Dropdown.Item>
                  ))}
                </Dropdown>
              </Dropdown>
            </Form.Group>
          </Col>
          <Col xs={12} lg={6} className='align-self-end'>
            <Button
              style={{ marginTop: '48px' }}
              variant='outline-success'
              type='submit'
              onClick={handleSearchSubmit}
            >
              Select Service
            </Button>
          </Col>
        </Row>

        <br />

        <div className='selected-services'>
          <h4>Selected Services:</h4>
          <br />

          {selectedServices.map((service) => (
            <div key={service} className='service-item'>
              <Row>
                <Col xs={6} lg={6}>
                  <span>{service}</span>
                </Col>

                <Col xs={6} lg={6}>
                  <Button
                    variant='outline-danger'
                    onClick={() => handleDropService(service)}
                  >
                    Drop
                  </Button>
                </Col>
              </Row>
              <hr />
            </div>
          ))}
        </div>
        <Button
        type="submit"> Submit </Button>
      </Form>
    </div>
  );
};

export default Create_event_template;
