import React, { useState } from 'react';
import { Formik, Field, Form } from "formik";

function YourComponent() {
  const [coverPhoto, setCoverPhoto] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    // Perform any validation or processing on the selected file if needed
    setCoverPhoto(URL.createObjectURL(file));
  };

  return (
    <div style={{ width: '100%', height: 'auto', marginTop: '5%', display: 'flex' }}>
      <div className="portfolio_Cover">
        <Row>
          <img src={coverPhoto} className="Portfolio_CoverPhoto" alt="Cover Photo" />
          <br />
          <br />
          <label htmlFor="coverPhotoInput">
            <Button
              style={{
                padding: '1px',
                width: '190px',
                marginTop: '-3%',
                marginLeft: '81.8%',
                backgroundColor: '#303030',
                color: 'white',
                borderColor: 'transparent',
              }}
            >
              Change Cover Photo
            </Button>
          </label>
          <input
            type="file"
            id="coverPhotoInput"
            style={{ display: 'none' }}
            onChange={handleFileChange}
          />
        </Row>
        <br />
      </div>
    </div>
  );
}

export default YourComponent;
