import React, { useEffect, useState } from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import axios from 'axios';
import './Create_Events_dropdown.css';

function BasicButtonExample() {
  const [eventTemplates, setEventTemplates] = useState([]);

  useEffect(() => {
    // Fetch event templates from the backend API
    axios.get('http://localhost:3001/evnttmplt/slctevnttmplt')
      .then(response => {
        setEventTemplates(response.data);
      })
      .catch(error => {
        console.error('Error fetching event templates:', error);
      });
  }, []);

  return (
    <DropdownButton id="dropdown-basic-button" title="Event Template" drop="down">
      {eventTemplates.map(template => (
        <Dropdown.Item key={template.id} href={`#/action-${template.id}`}>
          {template.eventTemplateName}
        </Dropdown.Item>
      ))}
    </DropdownButton>
  );
}

export default BasicButtonExample;
