import React, { useState } from 'react';
import { Col, Row } from 'react-bootstrap';
import './cust_reg.css';

function Cust_reg() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phoneNumber: '',
    email: '',
    address: '',
    gender: '',
    birthday: '',
    nicNumber: '',
    occupation: ''
  });

  const [formErrors, setFormErrors] = useState({
    firstName: '',
    lastName: '',
    phoneNumber: '',
    email: '',
    nicNumber: ''
  });

  const validateForm = () => {
    let isValid = true;
    const errors = {};

    // Check if all fields are filled (not null)
    for (const key in formData) {
      if (!formData[key]) {
        errors[key] = 'This field is required.';
        isValid = false;
      }
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (formData.email && !emailRegex.test(formData.email)) {
      errors.email = 'Invalid email format.';
      isValid = false;
    }

    // Validate phone number (Sri Lankan format: XXXXXXXXXX)
    const phoneRegex = /^[0-9]{10}$/;
    if (formData.phoneNumber && !phoneRegex.test(formData.phoneNumber)) {
      errors.phoneNumber = 'Invalid phone number format.';
      isValid = false;
    }

    // Validate NIC number (Sri Lankan format: XXXXXXXXXXV)
    const nicRegex = /^[0-9]{9}[Vv]$/;
    if (formData.nicNumber && !nicRegex.test(formData.nicNumber)) {
      errors.nicNumber = 'Invalid NIC number format.';
      isValid = false;
    }

    setFormErrors(errors);
    return isValid;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      // Perform form submission or further processing here
      console.log('Form is valid. Submitting...');
    } else {
      console.log('Form contains errors. Please fix them.');
    }
  };

  return (
    <div className="form-container">
      <h2 className="form-heading">Customer Registration Form</h2>

      <form onSubmit={handleSubmit}>
        <Row>
          <Col xs={12} lg={6}>
            <label>
              First Name:
              <input
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
              />
              {formErrors.firstName && <span className="error">{formErrors.firstName}</span>}
            </label>
          </Col>

          <Col xs={12} lg={6}>
            <label>
              Last Name:
              <input
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
              />
              {formErrors.lastName && <span className="error">{formErrors.lastName}</span>}
            </label>
          </Col>
        </Row>

        <Row>
          <Col xs={12} lg={12}>
            <label>
              Phone Number:
              <input
                type="text"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleChange}
              />
              {formErrors.phoneNumber && <span className="error">{formErrors.phoneNumber}</span>}
            </label>
          </Col>

          <Col xs={12} lg={12}>
            <label>
              E-mail:
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
              {formErrors.email && <span className="error">{formErrors.email}</span>}
            </label>
          </Col>
        </Row>

        <Row>
          <Col xs={12} lg={12}>
            <label>
              Address:
              <input
                type="text"
                name="address"
                value={formData.address}
                onChange={handleChange}
              />
              {formErrors.address && <span className="error">{formErrors.address}</span>}
            </label>
          </Col>
        </Row>

        <Row>
          <Col xs={12} lg={6}>
            <label>
              Gender:
              <br />
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="male"
                  checked={formData.gender === 'male'}
                  onChange={handleChange}
                />
                Male
              </label>
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="female"
                  checked={formData.gender === 'female'}
                  onChange={handleChange}
                />
                Female
              </label>
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="other"
                  checked={formData.gender === 'other'}
                  onChange={handleChange}
                />
                Other
              </label>
              {formErrors.gender && <span className="error">{formErrors.gender}</span>}
            </label>
          </Col>

          <Col xs={12} lg={6}>
            <label style={{ padding: '10% 0%' }}>
              Birthday:
              <input
                type="date"
                name="birthday"
                value={formData.birthday}
                onChange={handleChange}
              />
              {formErrors.birthday && <span className="error">{formErrors.birthday}</span>}
            </label>
          </Col>
        </Row>

        <Row>
          <Col xs={12} lg={6}>
            <label>
              NIC Number:
              <input
                type="text"
                name="nicNumber"
                value={formData.nicNumber}
                onChange={handleChange}
              />
              {formErrors.nicNumber && <span className="error">{formErrors.nicNumber}</span>}
            </label>
          </Col>
        </Row>

        <Row>
          <Col xs={12}>
            <label>
              Occupation:
              <input
                type="text"
                name="occupation"
                value={formData.occupation}
                onChange={handleChange}
              />
              {formErrors.occupation && <span className="error">{formErrors.occupation}</span>}
            </label>
          </Col>
        </Row>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Cust_reg;
