import React, { useEffect, useState } from 'react';
import { styled, alpha } from '@mui/material/styles';
import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';

const Search = styled('div')(({ theme }) => ({
  position: 'relative',
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  '&:hover': {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginLeft: 0,
  width: '100%',
  [theme.breakpoints.up('sm')]: {
    marginLeft: theme.spacing(1),
    width: 'auto',
  },
  border: `0px solid ${theme.palette.primary.main}`, // Add border
  outline: 'none', 
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: '100%',
  position: 'absolute',
  pointerEvents: 'none',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: 'inherit',
  '& .MuiInputBase-input': {
    padding: theme.spacing(2, 2, 2, 0),
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: '50%', // Adjust the width to accommodate the placeholder text
      '&:focus': {
        width: '20ch',
      },
    },
  },
}));

export default function SearchBar() {
  const [serviceNames, setServiceNames] = useState([]);
  const [searchValue, setSearchValue] = useState('');

  useEffect(() => {
    fetch('http://localhost:3001/gtservicenames')
      .then((response) => response.json())
      .then((data) => setServiceNames(data))
      .catch((error) => console.error('Error fetching service names:', error));
  }, []);

  const handleSearchChange = (event) => {
    setSearchValue(event.target.value);
  };

  return (
    <div>
      <Search style={{ width: '50%', backgroundColor: 'white' }}>
        <SearchIconWrapper>
          <SearchIcon />
        </SearchIconWrapper>
        <StyledInputBase
          placeholder="Search Services..."
          value={searchValue}
          onChange={handleSearchChange}
        />
      </Search>
      {serviceNames
        .filter((service) => service.name.includes(searchValue))
        .map((service) => (
          <div key={service.id}>{service.name}</div>
        ))}
    </div>
  );
}
