import React, { useState } from 'react';
import './top_nav.css'

function Buttontabs() {
  const [activeButton, setActiveButton] = useState('button-1'); // Initialize the active button state

  const handleButtonClick = (buttonId) => {
    setActiveButton(buttonId); // Update the active button when a button is clicked
  };

  return (
    <div className='first_section_buttontabs'>
      <div className="button-container">
        <button
          className={`button ${activeButton === 'button-1' ? 'active' : ''}`}
          onClick={() => handleButtonClick('button-1')}
        >
          My Events
        </button>
        <button
          className={`button ${activeButton === 'button-2' ? 'active' : ''}`}
          onClick={() => handleButtonClick('button-2')}
        >
          Past Events
        </button>
      </div>

      {/* Render the relevant component based on the active button */}
      {activeButton === 'button-1' ? <MyEventsComponent /> : null}
      {activeButton === 'button-2' ? <PastEventsComponent /> : null}
    </div>
  );
}

// Define the components for My Events and Past Events
function MyEventsComponent() {
  return (
    <div className="content">
      skajdkjakjsd
    </div>
  );
}

function PastEventsComponent() {
  return <div className="content">Past Events Component</div>;
}

export default Buttontabs;
