import React, { useState } from 'react';
import './createEventsForm.css';
import { Row, Col, Form, Button } from 'react-bootstrap';

const CreateEventsForm = () => {
  const [eventName, setEventName] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [estimatedBudget, setEstimatedBudget] = useState('');
  const [description, setDescription] = useState('');

  // const handleSubmit = (e) => {
  //   e.preventDefault();
  //   // Do something with the form data
  //   console.log('Event Name:', eventName);
  //   console.log('Event Date:', eventDate);
  //   console.log('Estimated Budget:', estimatedBudget);
  //   console.log('Description:', description);
  // };

  const handleSubmit = (data, { resetForm }) => {
    axios
      .post("http://localhost:3001/events", data)
      .then((response) => {
        setSubmissionStatus("success");
        resetForm();
      })
      .catch((error) => {
        setSubmissionStatus("error");
        console.log(error);
      });
  };








  return (
    <div>
      <h1 className="Events_CreateEvents_heading1">Create Your Event</h1>
      <div className='CreateEvents_form_full' style={{ overflowX: 'hidden' }}>

        <Form onSubmit={handleSubmit}>
          <Row>
            <Col>
              <Form.Group controlId="eventName">
                <Form.Label>Event Name :</Form.Label>
                <Form.Control
                  type="text"
                  value={eventName}
                  onChange={(e) => setEventName(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>

          </Row>
          <Row>
            <Col>
              <Form.Group controlId="eventDate">
                <Form.Label>Event Date :</Form.Label>
                <Form.Control
                  type="date"
                  value={eventDate}
                  onChange={(e) => setEventDate(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col>
              <Form.Group controlId="estimatedBudget">
                <Form.Label>Estimated Budget (Rs.) : </Form.Label>
                <Form.Control
                  type="text"
                  value={estimatedBudget}
                  onChange={(e) => setEstimatedBudget(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col>
              <Form.Group controlId="description">
                <Form.Label>Description :</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>
          <Button variant="primary" type="submit">
            Create Event
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default CreateEventsForm;
