import React, { useEffect, useState } from 'react';
import { Button } from 'react-bootstrap';
import axios from 'axios';

const Admin_View_Services = () => {
  const [services, setServices] = useState([]);

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const response = await axios.get('http://localhost:3001/addservices');
      setServices(response.data);
    } catch (error) {
      console.error('Error fetching services:', error);
    }
  };

  return (
    <div style={{ margin: '6% 4%' }}>
      <h1 className="create_event_template_heading">Manage Services</h1>
      <Button
        href="/services/add_services"
        style={{ backgroundColor: 'white', color: 'black', borderColor: 'transparent', marginLeft: '450px' }}
      >
        Add Service
      </Button>

      <table style={{ padding: '55%', marginTop: '3%' }}>
        <thead>
          <tr>
            <th>Service Name</th>
            <th>Actions</th>
          </tr>
        </thead>

        <tbody>
          {services.map((service) => (
            <tr key={service.id}>
              <td>{service.serviceName}</td>
              <td>
                <Button style={{ backgroundColor: 'red', color: 'white', borderColor: 'transparent', width: '80%' }}>
                  View
                </Button>
                <Button style={{ backgroundColor: '#303030', color: 'white', borderColor: 'transparent', width: '80%' }}>
                  Remove
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Admin_View_Services;
